class Command(BaseCommand):
    help = "Dump the template data"

    def handle(self, *args, **options):
        TemplatInfo.objects.all().delete()
        new_templates = [{**x, "data": json.dumps(x["data"])} for x in templates]
        TemplatInfo.objects.bulk_create([TemplatInfo(**x) for x in new_templates])
